mapper.select:
SELECT C.ENO, C.QUESTION_NO, C.CORRECT_YN, DBMS_LOB.SUBSTR(C.CONTENT, 20, 1) AS CONTENT, C.TYPE_CD AS TYPE, C.LEVEL_CD AS LEV, C.CATEGORY_NAME, C.POINTS, C.FST_CRT_USID, TO_CHAR(C.FST_CRT_DTTI, 'YYYY-MM-DD HH24:MI:SS') AS FST_CRT_DTTI, C.LT_CH_USID, TO_CHAR(C.LT_CH_DTTI, 'YYYY-MM-DD HH24:MI:SS') AS LT_CH_DTTI FROM ( SELECT A.ENO, A.QUESTION_NO, A.CORRECT_YN, B.CONTENT, B.TYPE_CD, B.LEVEL_CD, B.CATEGORY_NAME, B.POINTS, A.FST_CRT_USID, A.FST_CRT_DTTI, A.LT_CH_USID, A.LT_CH_DTTI, ROW_NUMBER() OVER (ORDER BY A.LT_CH_DTTI DESC) AS rn FROM QZ_TB_HISTORY A , QZ_TB_QUESTION B WHERE A.QUESTION_NO = B.QUESTION_NO AND A.ENO = #{eno} AND A.CORRECT_YN = #{correctYn}
AND B.CATEGORY_NAME = #{category}
AND B.LEVEL_CD = #{lev}
AND B.TYPE_CD = #{type}
AND B.CONTENT LIKE '%' || #{keyword} || '%'
) C WHERE C.rn BETWEEN #{startRow} AND #{endRow} ORDER BY C.LT_CH_DTTI DESC

SELECT count(1)
FROM QZ_TB_HISTORY A
, QZ_TB_QUESTION B
WHERE A.QUESTION_NO = B.QUESTION_NO
AND A.ENO = #{eno}
AND A.CORRECT_YN = #{correctYn}

    AND B.CATEGORY_NAME = #{category}


    AND B.LEVEL_CD = #{lev}


    AND B.TYPE_CD = #{type}


    AND B.CONTENT LIKE '%' || #{keyword} || '%'
